mapper.select:
SELECT * FROM (SELECT ROWNUM AS RN , deptId , deptName , mngId , locId FROM (SELECT DEPARTMENT_ID AS deptId , DEPARTMENT_NAME AS deptName , MANAGER_ID AS mngId , LOCATION_ID AS locId FROM DEPARTMENTS
ORDER BY deptId DESC
ORDER BY deptName DESC
ORDER BY mngId DESC
ORDER BY locId DESC
) ) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE DEPARTMENTS
   SET DEPARTMENT_NAME = #{deptName}
     , MANAGER_ID = #{mngId}
     , LOCATION_ID = #{locId}
 WHERE DEPARTMENT_ID = #{deptId}
