mapper.update:
UPDATE EMP_BOARDS
   SET VIEW_CNT = #{viewCnt} + 1
 WHERE ID = #{id}

mapper.select:
SELECT ID, EMPID, BID
	 , VIEWED
	 , LATEST_VIEW_DATE AS latestViewDate
  FROM EMP_BOARDS_STATICS
 WHERE EMPID = #{empId}
   AND BID = #{bid}

mapper.update:
UPDATE EMP_BOARDS_STATICS
   SET LATEST_VIEW_DATE = SYSDATE
 WHERE ID = #{id}
