mapper.select:
SELECT RN , empId , empName , email , jobName , jobId , deptName , deptId FROM (SELECT ROWNUM AS RN , E.EMPLOYEE_ID AS empId , CONCAT(FIRST_NAME, ' ' || LAST_NAME) AS empName , CONCAT(E.EMAIL, '@emp.com') AS email , J.JOB_TITLE AS jobName , J.JOB_ID AS jobId , D.DEPARTMENT_NAME AS deptName , D.DEPARTMENT_ID AS deptId FROM EMPLOYEES E JOIN JOBS J ON E.JOB_ID = J.JOB_ID JOIN DEPARTMENTS D ON E.DEPARTMENT_ID = D.DEPARTMENT_ID
WHERE E.DEPARTMENT_ID = #{deptId}
) WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
		UPDATE EMPLOYEES
		   SET EMAIL = SUBSTR(#{email}, 1, INSTR(#{email}, '@emp.com')-1)
 			 , FIRST_NAME = SUBSTR(#{empName}, 1, INSTR(#{empName}, ' ') - 1)
		     , LAST_NAME = SUBSTR(#{empName}, INSTR(#{empName}, ' ') + 1, LENGTH(#{empName}))
		     , JOB_ID = #{jobId}
		     , DEPARTMENT_ID = #{deptId}
		 WHERE EMPLOYEE_ID = #{empId}

UPDATE EMPLOYEES
   SET PHONE_NUMBER = #{phone}
     , SALARY = #{salary}
     , COMMISSION_PCT = #{commission}
     , HIRE_DATE = #{hireDate}
 WHERE EMPLOYEE_ID = #{empId}
